import React, { Component } from 'react';
import './Pokecard.css'
const POKE_API = 'https://assets.pokemon.com/assets/cms2/img/pokedex/detail/';

let padToThree = (number) => (number <= 999 ? `00${number}`.slice(-3) : number);
class Demo extends Component {
        
    render() { 
        let sr=`${POKE_API}${padToThree(this.props.id)}.png`;   
        return (<>
         
        <div className="Pokecard">
               
                <img src={sr} alt="PIKACHU"/>
                <h2 className="Pokecard-title">Name: {this.props.name}</h2>
                <h2 className="Pokecard-data">Type: {this.props.type}</h2>
                <h2 className="Pokecard-data">Exp: {this.props.exp}</h2>
        
        </div>
       </>);
}
}

export default  Demo;
